        <!-- More Styles -->
        <style>
        body {
            font-family: Arial, sans-serif;
            background-color: #f5f5f5;
            margin: 0;
            padding: 0;
        }

        /* Black banner with "The Vault" */
        header {
            background-color: black;
            color: #fff;
            text-align: center;
            padding: 20px;
            font-size: 24px;
        }

        .search-bar {
        display: flex;
        align-items: center;
        justify-content: flex-end; /* Move the search bar to the right */
    }

    .search-bar input {
        padding: 10px;
        margin-right: 10px;
        border: none;
        border-radius: 20px;
        font-size: 16px;
        width: 250px; /* Adjust width as needed */
    }

        .search-bar button {
            background-color: #007BFF;
            color: #fff;
            border: none;
            border-radius: 20px;
            padding: 10px 20px;
            cursor: pointer;
            font-size: 16px;
        }

        /* App card styles */
        .app-container {
            display: flex;
            flex-wrap: wrap;
            justify-content: space-between;
            padding: 20px; /* Add padding between app cards */
        }

        .app-card {
            width: calc(33.333% - 20px);
            margin-bottom: 20px;
            background-color: #fff;
            border-radius: 5px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
            transition: transform 0.2s;
            display: flex;
            flex-direction: column;
            align-items: center;
        }

        .app-card:hover {
            transform: scale(1.02);
        }

        /* Button styles */
        .app-button {
            background-color: #007BFF;
            color: #fff;
            padding: 10px 20px;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            transition: background-color 0.2s;
        }

        .app-button:hover {
            background-color: #0056b3;
        }

        /* Add some spacing between elements for better readability */
        .app-title, .app-subtitle, .app-description {
            margin-bottom: 10px;
        }

        /* Customize the scrollbar style (optional) */
        body::-webkit-scrollbar {
            width: 12px;
        }

        body::-webkit-scrollbar-thumb {
            background-color: #007BFF;
            border-radius: 10px;
        }

        body::-webkit-scrollbar-track {
            background: #f5f5f5;
        }

        /* Set a fixed size for the app images */
        .app-card img {
            max-width: 100px; /* Adjust the size as needed */
            max-height: 100px; /* Adjust the size as needed */
        }



