    <body>
    <header>
        The Vault
    </header>

    <!-- DIv for the search bar -->
    <div class="search-bar">
        <input id="searchInput" type="text" placeholder="Search..." oninput="filterApps()">
        <label for="categoryFilter">Filter by Category:</label>
        <select id="categoryFilter" onchange="filterByCategory()">
            <option value="All">All</option>
            <option value="Environmental">Environmental</option>
            <option value="Fitness">Fitness</option>
            <option value="Entertainment">Entertainment</option>
            <option value="Gaming">Gaming</option>
        </select>
    </div>


    <div class="app-container" id="appContainer">
    </div>

<!-- Modal -->
<div id="myModal" class="modal">
  <div class="modal-content">
    <span class="close">&times;</span>
    <h2 id="modal-title"></h2>
    <div class="app-details">
      <img id="modal-image" src="" alt="App Image">
      <p id="modal-category"></p>
      <p id="modal-description"></p>
      <p id="modal-organization"></p>
      <p id="modal-platforms"></p>
      <p id="modal-links"></p>
      <p id="modal-price"></p>
    </div>
  </div>
</div>


    <script>
        // Array of applications
        const applications = [
            {
                name: "EcoExplorer",
                category: "Environmental",
                description: " EcoExplorer is a must-have app for environmentally conscious individuals...",
                image: "{{ asset('images/ecofriendlyappicon.jpg') }}",
                organization: "EcoTech Inc.",
                platforms: "iOS, Android",
                links: "https://example.com/ecoexplorer",
                price: "$4.99",
            },
            {
                name: "FitFusion",
                category: "Fitness",
                description: "FitFusion is the ultimate fitness app that combines the best of various workout styles...",
                image: "{{ asset('images/fitnessappicon.jpg') }}",
                organization: "FitTech Solutions",
                platforms: "iOS, Android",
                links: "https://example.com/fitfusion",
                price: "$5.99",
            },
            {
                name: "MindMaze",
                category: "Entertainment",
                description: " MindMaze is a captivating and immersive puzzle adventure game...",
                image: "{{ asset('images/puzzlegameicon.jpg') }}",
                organization: "MindGaming Co.",
                platforms: "iOS, Android",
                links: "https://example.com/mindmaze",
                price: "$2.99",
            },
            {
            name: "Subway Runner",
                category: "Gaming",
                description: " Subway Runner is a fun chasing game...",
                image: "{{ asset('images/runnericon.png') }}",
                organization: "FunZone Studios",
                platforms: "Android",
                links: "https://example.com/subwayrunner",
                price: "$1.99",
                
         },
            {
            name: "Street Shooter",
                category: "Gaming",
                description: " Street shooter is a fun shooting game...",
                image: "{{ asset('images/streetshootericon.png') }}",
                organization: "ActionGames Inc.",
                platforms: "iOS",
                links: "https://example.com/streetshooter",
                price: "$3.49",
            },
            {
                name: "CalTrack",
                category: "Fitness",
                description: " CalTrack is the a great calorie counting fitness app that allows users to reach their weight goals...",
                image: "{{ asset('images/caltrackicon.webp') }}",
                organization: "HealthTech Labs",
                platforms: "iOS, Android",
                links: "https://example.com/caltrack",
                price: "$3.99",
            },
        ];

      
// Code for the modal that pops up when an application is clicked
// Get the modal element
const modal = document.getElementById("myModal");
// Get the <span> element that closes the modal
const closeBtn = document.querySelector(".close");

// Updated displayModal function to populate modal with specific app details
function displayModal(name, image, category, description, organization, platforms, links, price) {
    const modalTitle = document.getElementById("modal-title");
    const modalImage = document.getElementById("modal-image");
    const modalCategory = document.getElementById("modal-category");
    const modalDescription = document.getElementById("modal-description");
    const modalOrganization = document.getElementById("modal-organization");
    const modalPlatforms = document.getElementById("modal-platforms");
    const modalLinks = document.getElementById("modal-links");
    const modalPrice = document.getElementById("modal-price");

    // Physically displays all of the given data into the modal card
    modalTitle.textContent = name;
    modalImage.src = image;
    modalCategory.textContent = `Category: ${category}`;
    modalDescription.textContent = `Description: ${description}`;
    modalOrganization.textContent = `Organization: ${organization}`;
    modalPlatforms.textContent = `Platforms: ${platforms}`;
    modalLinks.textContent = `Links: ${links}`;
    modalPrice.textContent = `Price: ${price}`;

    modal.style.display = "block";
}

// Function to close the modal
function closeModal() {
    modal.style.display = "none";
}

// Event listener to close the modal when clicking on the close button
closeBtn.addEventListener("click", closeModal);

// Update onclick event in the displayApplications function
// Updated displayApplications function to populate modal with app details
function displayApplications(apps) {
    const appContainer = document.getElementById("appContainer");
        
    appContainer.innerHTML = "";

    apps.forEach(app => {
        const appCard = document.createElement("div");
        appCard.classList.add("app-card");
        appCard.innerHTML = `
            <img src="${app.image}" alt="${app.name} Image">
            <h2 class="app-title">${app.name}</h2>
            <h3 class="app-subtitle">${app.category}</h3>
            <button class="app-button" onclick="displayModal('${app.name}', '${app.image}', '${app.category}', '${app.description}', '${app.organization}', '${app.platforms}', '${app.links}', '${app.price}')">View Details</button>
        `;
        appContainer.appendChild(appCard);
    });
}
    // Call displayApplications to initially display apps
    displayApplications(applications);



        // function to show app filter 
        function filterApps() {
            const searchInput = document.getElementById("searchInput").value.toLowerCase();
            const filteredApps = applications.filter(app => app.name.toLowerCase().includes(searchInput));
            displayApplications(filteredApps);
        }

        // Function to filter apps by category
        function filterByCategory() {
            const selectedCategory = document.getElementById("categoryFilter").value;
            if (selectedCategory === "All") {
                displayApplications(applications);
            } else {
                const filteredApps = applications.filter(app => app.category === selectedCategory);
                displayApplications(filteredApps);
            }
        }
   
